package bootstraplogging

import (
	"github.com/openshift/installer/pkg/asset"
)

const (
	genevaCredentialsFilename = "bootstraplogging.yaml"
)

// Config generates the bootstraplogging.yaml file.
type Config struct {
	Certificate       string
	Key               string
	Namespace         string
	Environment       string
	ConfigVersion     string
	ResourceID        string
	SubscriptionID    string
	Region            string
	ResourceName      string
	ResourceGroupName string
	File              *asset.File
	FluentbitImage    string
	MdsdImage         string
}

var _ asset.WritableAsset = (*Config)(nil)

// Dependencies returns all of the dependencies directly needed by an
// Config asset.
func (a *Config) Dependencies() []asset.Asset {
	return []asset.Asset{}
}

// Generate generates the bootstraplogging.yaml file.
func (a *Config) Generate(parents asset.Parents) error {
	return nil
}

// Name returns the human-friendly name of the asset.
func (a *Config) Name() string {
	return "Geneva Credentials"
}

// Files returns the files generated by the asset.
func (a *Config) Files() []*asset.File {
	if a.File != nil {
		return []*asset.File{a.File}
	}
	return []*asset.File{}
}

// Load returns the installconfig from disk.
func (a *Config) Load(f asset.FileFetcher) (found bool, err error) {
	return true, nil
}
